mapper.select:
SELECT




FROM credential c
WHERE c.id = #{id}

mapper.update:
UPDATE credential c

  c.username = #{username},
  c.password = #{password},
  c.type = #{type},
  c.confirmed = #{type}

WHERE c.id = #{id}

mapper.select:
SELECT




FROM credential c


    c.id = #{id}


    AND c.username = #{username}


    AND c.type = #{type}


    AND c.confirmed = #{confirmed}


    AND c.user_id = #{userId}
